/bin/mkdir -m 0755 /root/rolltests


#!/usr/bin/perl -w

use Test::More qw(no_plan);

my $appliance = $#ARGV >= 0 ? $ARGV[0] :
                -d '/export/rocks/install' ? 'Frontend' : 'Compute';
my $isCompute = $appliance eq 'Compute';
my $isFe = $appliance eq 'Frontend';
my $isLogin = $appliance eq 'Login';
my $output;
my $TESTFILE = 'tmppgi';

open(OUT, ">$TESTFILE.c");
print OUT <<END;
#include <stdio.h>
int main(char **args) {
  printf("Hello world\\n");
  return 0;
}
END
open(OUT, ">$TESTFILE.f");
print OUT <<END;
      PROGRAM HELLO
      PRINT*, 'Hello world!'
      END
END
close(OUT);

ok(-d '/opt/pgi', 'pgi compiler installed');
my $LOADPGI = `$ENV{MODULESHOME}/bin/modulecmd bash load pgi`;
ok($? == 0, 'pgi module installed');

$output = `$LOADPGI 2>&1; pgcc -o $TESTFILE $TESTFILE.c 2>&1`;
ok($? == 0, 'pgi C compiler works');
$output = `./$TESTFILE`;
ok($? == 0, 'compiled C program runs');
like($output, qr/Hello world/, 'compile C program correct output');

$output = `$LOADPGI 2>&1; pgf77 -o $TESTFILE $TESTFILE.f 2>&1`;
ok($? == 0, 'pgi FORTRAN compiler works');
$output = `./$TESTFILE`;
ok($? == 0, 'compiled FORTRAN program runs');
like($output, qr/Hello world/, 'compile FORTRAN program correct output');

`rm -f $TESTFILE*`;
